import React, { useState, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import FinnishBankUtils from 'finnish-bank-utils'; // Make sure this import is correct
import Barcode from 'react-barcode';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { format } from 'date-fns';


function IbanForm() {
  // Initialize states with empty values for user input
  const [iban, setIban] = useState('');
  const [sum, setSum] = useState('');
  const [dueDate, setDueDate] = useState(new Date()); // now dueDate contains today's date
  const [validationState, setValidationState] = useState('');
  const [barcode, setBarcode] = useState('');
  const [error, setError] = useState('');
  const barcodeRef = useRef(null);
  const referenceNumber = '55958 22432 94671'; // Example of a hard-coded valid reference number



  const handleDateChange = (date) => {
    setDueDate(date);
  };


  const copyToClipboard = (e) => {
    barcodeRef.current.select();
    document.execCommand('copy');
    e.target.focus();
    // Optionally, you can show a tooltip/popover to indicate the text was copied.
  };

  const handleIbanChange = (e) => {
    const cursorPosition = e.target.selectionStart;
    const previousValue = iban;
    const currentValue = e.target.value;

    // Remove all spaces for consistency in the transformation logic
    const rawValue = currentValue.replace(/ /g, '');

    // Add a space after every 4th character
    const formattedValue = rawValue.replace(/(.{4})/g, '$1 ').trim();

    setIban(formattedValue);

    // Clear any previous errors
    setError('');

    // Handle cursor position for a smoother editing experience
    let newCursorPosition = cursorPosition;
    if (currentValue.length > previousValue.length) {
      // The user is typing a new character
      if ((cursorPosition - 1) % 5 === 0) {
        // The user typed right before a space or right after a group of 4 characters. Move the cursor forward by 1 (past the space).
        newCursorPosition += 1;
      }
    } else {
      // The user is deleting a character
      if (cursorPosition % 5 === 0) {
        // The user deleted right after a space. Move the cursor back by 1 (before the space).
        newCursorPosition -= 1;
      }
    }

    // After setting the state, we adjust the cursor position to ensure it doesn't jump
    requestAnimationFrame(() => {
      e.target.setSelectionRange(newCursorPosition, newCursorPosition);
    });

    // Validation logic (if applicable)
    if (formattedValue.length === 22) {
      validateIban(formattedValue); // You could validate the IBAN here
    } else {
      setValidationState(''); // Reset validation state if the length requirement is not met
    }
  };



  const validateIban = (iban) => {
    // Implement your validation logic here
    const isValidIban = FinnishBankUtils.isValidFinnishIBAN(iban.replace(/\s/g, '')); // validation without spaces
    setValidationState(isValidIban ? 'is-valid' : 'is-invalid');

    if (!isValidIban) {
      setError('Invalid IBAN');
    }
  };


  const validateAndGenerateBarcode = () => {

    const parsedSum = parseFloat(sum);
    if (isNaN(parsedSum)) {
      setError('Invalid sum. Please provide a numeric value.');
      return;
    }

    // Format the date in the format "dd.MM.yyyy"
    const formattedDate = format(dueDate, 'dd.MM.yyyy');

    // Generate barcode
    try {
      const generatedBarcode = FinnishBankUtils.formatFinnishVirtualBarCode({
        iban: iban,
        sum: parsedSum,
        reference: referenceNumber,
        date: formattedDate, // pass the formatted date string here instead of the Date object
      });
      setBarcode(generatedBarcode);
    } catch (error) {
      console.error("Error generating barcode:", error);
      setError('There was an error generating the barcode. Please check your inputs and try again.');
    }
  };


  return (
    <div className="container mt-5">
<div className="mb-3">
  <label htmlFor="ibanField" className="form-label">IBAN</label>
  <input 
    type="text"
    className={`form-control ${validationState}`}
    id="ibanField"
    value={iban}
    onChange={handleIbanChange}
    placeholder="Enter IBAN"
    maxLength="22" 
  />
  {validationState === 'is-invalid' && <div className="invalid-feedback">Invalid IBAN.</div>}
</div>
      <div className="mb-3">
  <label htmlFor="sumField" className="form-label">Amount (â‚¬)</label>
  <input 
    type="number" 
    className="form-control"
    id="sumField"
    value={sum}
    onChange={(e) => setSum(e.target.valueAsNumber || '')} // Ensuring we're working with a number, and prevent null on deletion
    placeholder="Enter amount in EUR"
    min="0" // Prevents negative amounts
    step="0.01" // Allows cents to be entered
    pattern="\d+(\.\d{2})?" // This pattern demands at least the cents part to be entered, you can remove it if you want more flexibility
    inputMode="decimal" // Brings up the numeric keypad on supported devices, useful for monetary values
  />
</div>
<div className="mb-3">
  <label htmlFor="dueDateField" className="form-label">Due Date</label>
  <br />
  <DatePicker
    selected={dueDate}
    onChange={handleDateChange}
    dateFormat="dd.MM.yyyy"
    className="form-control"
    id="dueDateField"
  />
</div>
      <div className="mb-3">
        <button 
          className="btn btn-primary" 
          onClick={validateAndGenerateBarcode}
        >
          Generate Barcode
        </button>
      </div>

      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

  {barcode && (

        <div className="input-group mb-3">
          <input 
            type="text"
            ref={barcodeRef}
            value={barcode}
            className="form-control"
            readOnly
          />
          <button 
            className="btn btn-outline-secondary" 
            type="button" 
            onClick={copyToClipboard}
          >
            Copy
          </button>
          <div className="mb-3">
          <label htmlFor="barCode" className="form-label">Barcode</label>

          <Barcode value={barcode} format="CODE128" />
        </div>
        </div>

      )}
    </div>
  );

}

export default IbanForm;
