// src/App.js
import React, { useState } from 'react';
import { Button, Form, InputGroup, FormControl } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { isValidIBAN } from 'finnish-bank-utils';

const FinnishBankUtils = require('finnish-bank-utils');

function App() {
  const [iban, setIban] = useState('');
  const [validationState, setValidationState] = useState(null);

  const validateIban = () => {
    if (FinnishBankUtils.isValidFinnishIBAN(iban)) {
      setValidationState('is-valid');
    } else {
      setValidationState('is-invalid');
    }
};

  return (
    <div className="App m-3">
          <script src="https://unpkg.com/finnish-bank-utils/finnish-bank-utils.min.js"></script>

      <h1>IBAN Validator</h1>
      <Form>
        <InputGroup className="mb-3">
          <FormControl
            placeholder="Enter IBAN"
            aria-label="IBAN"
            value={iban}
            onChange={(e) => setIban(e.target.value)}
            className={validationState}
          />
          <Button onClick={validateIban} variant="outline-secondary">
            Validate
          </Button>
        </InputGroup>
        {validationState === 'is-invalid' && (
          <div className="text-danger">Invalid IBAN number.</div>
        )}
      </Form>
    </div>

  );
}

export default App;
