import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import FinnishBankUtils from 'finnish-bank-utils'; // Make sure this import is correct

function IbanForm() {
  // Initialize states with empty values for user input
  const [iban, setIban] = useState('');
  const [sum, setSum] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [validationState, setValidationState] = useState('');
  const [barcode, setBarcode] = useState('');
  const [error, setError] = useState('');
  const barcodeRef = useRef(null);

  const copyToClipboard = (e) => {
    barcodeRef.current.select();
    document.execCommand('copy');
    e.target.focus();
    // Optionally, you can show a tooltip/popover to indicate the text was copied.
  };

  const handleIbanChange = (e) => {
    setIban(e.target.value);
    setValidationState(''); // Reset validation state when the user changes the input
    setError(''); // Clear any previous error
  };

  const validateAndGenerateBarcode = () => {
    const isValidIban = FinnishBankUtils.isValidFinnishIBAN(iban);
    setValidationState(isValidIban ? 'is-valid' : 'is-invalid');

    if (!isValidIban) {
      setError('Invalid IBAN');
      return;
    }

    const parsedSum = parseFloat(sum);
    if (isNaN(parsedSum)) {
      setError('Invalid sum. Please provide a numeric value.');
      return;
    }

    // Here you can define a hard-coded or randomly generated reference number
    const referenceNumber = '55958 22432 94671'; // Example of a hard-coded valid reference number

    // Generate barcode
    try {
      const generatedBarcode = FinnishBankUtils.formatFinnishVirtualBarCode({
        iban: iban,
        sum: parsedSum,
        reference: referenceNumber, // Using the hard-coded or randomly generated reference number here
        date: dueDate,
      });
      setBarcode(generatedBarcode);
    } catch (error) {
      console.error("Error generating barcode:", error);
      setError('There was an error generating the barcode. Please check your inputs and try again.');
    }
  };

  return (
    <div className="container mt-5">
      <div className="mb-3">
        <label htmlFor="ibanField" className="form-label">IBAN</label>
        <input 
          type="text"
          className={`form-control ${validationState}`}
          id="ibanField"
          value={iban}
          onChange={handleIbanChange}
          placeholder="Enter IBAN"
        />
        {validationState === 'is-invalid' && <div className="invalid-feedback">Invalid IBAN.</div>}
      </div>
      <div className="mb-3">
        <label htmlFor="sumField" className="form-label">Sum</label>
        <input 
          type="number" 
          className="form-control"
          id="sumField"
          value={sum}
          onChange={(e) => setSum(e.target.value)}
          placeholder="Enter Sum"
        />
      </div>
      <div className="mb-3">
        <label htmlFor="dueDateField" className="form-label">Due Date (DD.MM.YYYY)</label>
        <input 
          type="text" 
          className="form-control"
          id="dueDateField"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
          placeholder="Enter Due Date"
        />
      </div>
      <div className="mb-3">
        <button 
          className="btn btn-primary" 
          onClick={validateAndGenerateBarcode}
        >
          Generate Barcode
        </button>
      </div>

      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

  {barcode && (
        <div className="input-group mb-3">
          <input 
            type="text"
            ref={barcodeRef}
            value={barcode}
            className="form-control"
            readOnly
          />
          <button 
            className="btn btn-outline-secondary" 
            type="button" 
            onClick={copyToClipboard}
          >
            Copy
          </button>
        </div>
      )}
    </div>
  );

}

export default IbanForm;
