import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // This line can be in your index.js or App.js file instead

const FinnishBankUtils = require('finnish-bank-utils');

function IbanForm() {
  const [iban, setIban] = useState('');
  const [sum, setSum] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [referenceNumber, setReferenceNumber] = useState('');
  const [validationState, setValidationState] = useState('');
  const [barcode, setBarcode] = useState('');


  const handleIbanChange = (e) => {
    setIban(e.target.value);
    setValidationState(''); // Reset validation state when the user changes the input
  };

  const validateAndGenerateBarcode = () => {
    const isValidIban = FinnishBankUtils.isValidFinnishIBAN(iban);
    setValidationState(isValidIban ? 'is-valid' : 'is-invalid');

    if (isValidIban) {
      // Generate barcode
      try {
        const generatedBarcode = FinnishBankUtils.formatFinnishVirtualBarCode({
          iban: iban,
          sum: parseFloat(sum),
          reference: referenceNumber,
          date: dueDate,
        });
        setBarcode(generatedBarcode);
      } catch (error) {
        console.error("Error generating barcode:", error);
      }
    }
  };

  return (
    <div className="container mt-5">
      <div className="mb-3">
        <label className="form-label">IBAN</label>
        <input 
          type="text"
          className={`form-control ${validationState}`}
          value={iban}
          onChange={handleIbanChange}
          placeholder="Enter IBAN"
        />
      </div>
      <div className="mb-3">
        <label className="form-label">Sum</label>
        <input 
          type="number" 
          className="form-control"
          value={sum}
          onChange={(e) => setSum(e.target.value)}
          placeholder="Enter Sum"
        />
      </div>
      <div className="mb-3">
        <label className="form-label">Due Date (DD.MM.YYYY)</label>
        <input 
          type="text" 
          className="form-control"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
          placeholder="Enter Due Date"
        />
      </div>
      <div className="mb-3">
        <label className="form-label">Reference Number</label>
        <input 
          type="text" 
          className="form-control"
          value={referenceNumber}
          onChange={(e) => setReferenceNumber(e.target.value)}
          placeholder="Enter Reference Number"
        />
      </div>
      <button 
        className="btn btn-primary" 
        onClick={validateAndGenerateBarcode}
      >
        Generate Barcode
      </button>

      {barcode && (
        <div className="mt-3">
          <h3>Generated Barcode:</h3>
          <p className="text-break">{barcode}</p>
        </div>
      )}
    </div>
  );
}

export default IbanForm;
