import React, { useState } from 'react';
const FinnishBankUtils = require('finnish-bank-utils');

function IbanForm() {
  const [iban, setIban] = useState('');
  const [sum, setSum] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [referenceNumber, setReferenceNumber] = useState('');
  const [validationState, setValidationState] = useState('');
  const [barcode, setBarcode] = useState('');

  const validateAndGenerateBarcode = () => {
    if (FinnishBankUtils.isValidFinnishIBAN(iban)) {
      setValidationState('is-valid');

      // Assuming the sum, dueDate, and referenceNumber are valid, generate the barcode
      const generatedBarcode = FinnishBankUtils.formatFinnishVirtualBarCode({
        iban: iban,
        sum: parseFloat(sum), // Convert to a float value
        reference: referenceNumber,
        date: dueDate, // Date should be in 'DD.MM.YYYY' format
      });

      setBarcode(generatedBarcode);
    } else {
      setValidationState('is-invalid');
    }
  };

  return (

    <div>
      <input 
        type="text" 
        value={iban} 
        onChange={(e) => setIban(e.target.value)} 
        className={`form-control ${validationState}`}
        placeholder="Enter IBAN"
      />
      <input 
        type="text" 
        value={sum} 
        onChange={(e) => setSum(e.target.value)} 
        className="form-control"
        placeholder="Enter Sum"
      />
      <input 
        type="text" 
        value={dueDate} 
        onChange={(e) => setDueDate(e.target.value)} 
        className="form-control"
        placeholder="Enter Due Date (DD.MM.YYYY)"
      />
      <input 
        type="text" 
        value={referenceNumber} 
        onChange={(e) => setReferenceNumber(e.target.value)} 
        className="form-control"
        placeholder="Enter Reference Number"
      />
      <button onClick={validateAndGenerateBarcode} className="btn btn-primary">
        Generate Barcode
      </button>

      {barcode && (
        <div>
          <h3>Generated Barcode:</h3>
          <p>{barcode}</p>
        </div>
      )}
    </div>
  );
}

export default IbanForm;
